def sub_months(era_t_yr):
    """Swap in the time axis so the datasets auto-align
    """
    clim_t['time'] = era_t_yr.time
    return era_t_yr - clim_t
    
delta_t = era_weather.temperature_2m.groupby('time.year').apply(sub_months)


